import React, { useEffect, useState } from 'react'

function App() {
  const [backendData, setBackendData] = useState([{}])

  useEffect(() => {
    fetch("/api").then(
      response => response.json()
    ).then(
      data => {
        setBackendData(data)
      }
    )
  }, [])

  const initialVlaues = {option: ""};
    const [formValues, setFormValues]= useState(initialVlaues)
    const [formErrors, setFormErrors] = useState({})
    const [isSubmit, setIsSubmit] = useState(false);

    const handleChange = (e) => {
        console.log(e.target);
        const { name, value } = e.target
        setFormValues({...formValues, [name]:value});
        console.log(formValues)
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        setIsSubmit(true);
        if(isSubmit){
            alert(`Submitted info: \n\nName: ${formValues.name}\nAge: ${formValues.age}\nGender: ${formValues.gender}\nEmail: ${formValues.email}\nRemark: ${formValues.remark}`);
        }
    }

    useEffect(() => {
        console.log(formErrors);
        if(Object.keys(formErrors).length == 0 && isSubmit) {
            console.log(formValues);
        }
    }, [formErrors] );


  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div class="form-group" style={{textAlign: "center"}}>
          <label for="gender">Option:</label>
          <select 
          style={{margin: "auto", display: "block", width: "40%"}}
          type="text"
          name="gender"
          value={ formValues.option }
          class="form-control" id="age"
          onChange={handleChange}>
            <option value="">----------------------------------------------------------------</option>
            <option value="User">User</option>
            <option value="Admin">Admin</option>
          </select>
          <br></br>
          <button type="submit" class="btn btn-primary">Submit</button>
        </div>

      </form>


      {(typeof backendData.users === 'undefined') ? (
        <p>Loading...</p>
      ): (
        backendData.users.map((user, i) => (
          <p key={i}>{user}</p>
        ))
      )}

    </div>
  )
}

  export default App;
