import React, {useEffect, useState} from 'react';
import ReactDOM from 'react-dom/client';

function Jiaxin() {
    const initialVlaues = {name: "", age: ""};
    const [formValues, setFormValues]= useState(initialVlaues)
    const [formErrors, setFormErrors] = useState({})
    const [isSubmit, setIsSubmit] = useState(false);

    const handleChange = (e) => {
        console.log(e.target);
        const { name, value } = e.target
        setFormValues({...formValues, [name]:value});
        console.log(formValues)
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        setFormErrors(validate(formValues));
        setIsSubmit(true);
    }

    useEffect(() => {
        console.log(formErrors);
        if(Object.keys(formErrors).length == 0 && isSubmit) {
            console.log(formValues);
        }
    }, [formErrors] );

    const validate = (values) => {
        const errors = {}
        const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
        if(!values.name){
            errors.name = "Name is required";
        }
        if(!values.age){
            errors.age = "Age is required";
        }
        if(!values.gender){
            errors.gender = "Age is required";
        }
        return errors;
    }

    return(
        <div className='App'>
            <pre>{ JSON.stringify(formValues, undefined, 2) }</pre>
            <form onSubmit={handleSubmit}>
                <h1>Information form</h1>
                <div className='ui divider'></div>
                <div className='ui form'></div>
                <div className='field'>
                    <label>Name:</label>{' '}
                    <input type='text' name='name' placeholder='name' value={ formValues.name }
                    onChange={handleChange}/>
                    <p style={{color: "red"}}>{formErrors.name}</p>
                </div>
                <div className='field'>
                    <label>Age:</label>{' '}
                    <input type='number' min="0" name='age' placeholder='age' value={ formValues.age }
                    onChange={handleChange}/>
                    <p style={{color: "red"}}>{formErrors.age}</p>
                </div>
                <div className='field'>
                    <label>Gender:</label>{' '}
                    <select 
                    type="text"
                    name="gender"
                    value={ formValues.gender }
                    onChange={handleChange}>
                        <option value="">-----------</option>
                        <option value="Male">Male</option>
                        <option value="Female">Female</option>
                    </select>
                    <p style={{color: "red"}}>{formErrors.gender}</p>
                </div>
                <br></br>    
                <button className='fluid ui button blud'>Submit</button>
            </form>
        </div>
    )
}

export default Jiaxin;
