        console.log(formValues)
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        ValidityState(formValues)
    }
    return(
        <div className='App'>
            <pre>{ JSON.stringify(formValues, undefined, 2) }</pre>
            <form>
                <h1>Information form</h1>
                <div className='ui divider'></div>
                <div className='ui form'></div>
